import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Card, Row } from "react-bootstrap";
import { setState, useEffect } from "react";

function App() {
  let [news, setNews] = setState([]);

  useEffect(() => {
    fetch(
      "https://newsapi.org/v2/everything?q=tesla&from=2021-08-28&sortBy=publishedAt&apiKey=90dbf1958cc7423da14e4d90356138ab"
    )
      .then(res => res.json())
      .then(data => setNews(data));
  }, []);

  return (
    <Row xs={1} md={2} className="g-4">
      {Array.from({ length: 2 }).map((_, idx) => news.map(nw => <Card />))}
    </Row>
  );
}

export default App;
